using System;
using System.Linq;
using System.IO;
using System.Text;
using System.Collections;
using System.Collections.Generic;

/**
 * Auto-generated code below aims at helping you parse
 * the standard input according to the problem statement.
 **/
class Solution
{ 
    static void Main(string[] args)
    {
        const string L = "ABCDEFGHIJKLMNOPQRSTUVWXYZabcdefghijklmnopqrstuvwxyz";

        // Get number of sentences
        int N = int.Parse(Console.ReadLine());

        while(N>0)
        {
            //Get sentence
            string Sentence = Console.ReadLine();

            int SL = Sentence.Length;            
            string  Letters = string.Empty;

            while(SL>0)
            {
                string letter = Sentence[0].ToString();

                Sentence = Sentence.Replace(letter,string.Empty);

                if(L.Contains(letter) && (SL-Sentence.Length)>1)
                    Letters+=letter;

                SL = Sentence.Length;
            }

            if(Letters==String.Empty)
                Letters="NONE";

            Console.WriteLine(Letters);
            N--;
        }
    }
}
