import sys
import math

# Auto-generated code below aims at helping you parse
# the standard input according to the problem statement.

def dist(A,B):
    return (A[0]-B[0])**2+(A[1]-B[1])**2
def vec(A,B):
    return B[0]-A[0],B[1]-A[1]

n=int(input())
phrase="%s is a %s."

for _ in range(n):
    quadri=input().split()
    points={quadri[i]:(int(quadri[i+1]),int(quadri[i+2])) for i in range(0,12,3)}
    nom="".join(quadri[::3])
    v01=vec(points[nom[0]],points[nom[1]])
    v32=vec(points[nom[3]],points[nom[2]])
    if v01==v32:
        l01=dist(points[nom[0]],points[nom[1]])
        l12=dist(points[nom[1]],points[nom[2]])
        l13=dist(points[nom[1]],points[nom[3]])
        l02=dist(points[nom[0]],points[nom[2]])
        if l01==l12 and l13==l02:
            print(phrase%(nom,"square"))
        elif l01==l12:
            print(phrase%(nom,"rhombus"))
        elif l13==l02:
            print(phrase%(nom,"rectangle"))
        else:
            print(phrase%(nom,"parallelogram"))
    else:
        print(phrase%(nom,"quadrilateral"))
