luhnify = lambda digit: sum(divmod( digit*2, 10 ))

def luhn_checksum( digits ):
    digits = list(map( int, digits ))
    odds, evens = digits[-2::-2], digits[-1::-2]
    return sum( list(map(luhnify,odds)) + evens ) % 10

n = int(input())
for i in range(n):
    card = input()
    digits=card.replace(' ','')
    if luhn_checksum(digits) == 0:
        print('YES')
    else:
        print('NO')
