import java.util.*;

class Solution {

    public static void main(String args[]) {
        Scanner in = new Scanner(System.in);
        String A = in.next();
        String B = in.next();
        int L = in.nextInt();
        float prog = in.nextFloat();
        System.out.println(progressBar(A,B,prog,L));
    }

    public static String progressBar(String progChar, String incomplete, double progress, int length) {
		String disp = "";
		int len = length;
		for (float i = 0; i < len; i++) {
			if (i / len < progress) {
				disp = disp + progChar;
			} else {
				disp = disp + incomplete;
			}
		}
		return disp;
    }
}
