m = [[0] * 100 for _ in range(100)]
n = int(input())
pile_height=0
for i in range(n):
	x, y, v, w, h = [int(j) for j in input().split()]
	z = 0
	for k in range(y,y+w):
		for j in range(x,x+v):
		    z = max(z,m[k][j])
	for k in range(y,y+w):
		for j in range(x,x+v):
			m[k][j]=z+h
	pile_height=max(pile_height,z+h)
print(pile_height)
