import java.util.ArrayList;
import java.util.Collections;
import java.util.HashMap;
import java.util.List;
import java.util.Scanner;

class Solution {

	@SuppressWarnings("resource")
	public static void main(String args[]) {
		Scanner in = new Scanner(System.in);
		int players = in.nextInt();

		HashMap<String, Tribute> tributeCache = new HashMap<>();

		for (int i = 0; i < players; i++) {
			String name = in.next();
			Tribute t = new Tribute(name);
			tributeCache.put(name, t);
		}

		int turns = in.nextInt();

		for (int i = 0; i <= turns; i++) {
			String action = in.nextLine();
			Tribute killer = tributeCache.get(action.split(" ")[0]);
			for (int a = 2; a < action.split(" ").length; a++) {
				killer.kill(tributeCache.get(action.split(" ")[a].replace(",", "")));
			}
		}

		int i = 0;

		List<String> alphabatized = new ArrayList<String>(tributeCache.keySet());

		Collections.sort(alphabatized);
		for (String name : alphabatized) {
			Tribute t = tributeCache.get(name);
			String killer = "Winner", victims = "";
			if (!t.isAlive())
				killer = t.getKiller().getName();
			if (t.getVictims() != null && !t.getVictims().isEmpty()) {
				List<String> vs = new ArrayList<String>();
				for (Tribute killed : t.getVictims())
					vs.add(killed.getName());
				Collections.sort(vs);
				for (String killed : vs) {
					victims += killed + ", ";
				}
				victims = victims.substring(0, victims.length() - 2);
			} else {
				victims = "None";
			}
			System.out.println("Name: " + t.getName());
			System.out.println("Killed: " + victims);
			System.out.println("Killer: " + killer);
			if (i < players - 1)
				System.out.println("");
			i++;
		}
	}

	public static class Tribute {
		private String name;
		private boolean alive;

		private Tribute killer;

		private ArrayList<Tribute> victims;

		public Tribute(String name) {
			this.name = name;
			this.killer = null;
			this.alive = true;
			this.victims = new ArrayList<>();
		}

		public String getName() {
			return name;
		}

		public void kill(Tribute victim) {
			victims.add(victim);
			victim.setAlive(false);
			victim.setKiller(this);
		}

		public boolean isAlive() {
			return alive;
		}

		public void setAlive(boolean alive) {
			this.alive = alive;
		}

		public Tribute getKiller() {
			return killer;
		}

		public void setKiller(Tribute killer) {
			this.killer = killer;
		}

		public List<Tribute> getVictims() {
			return victims;
		}
	}
}
