using System;
using System.Linq;
using System.IO;
using System.Text;
using System.Collections;
using System.Collections.Generic;

/**
 * Auto-generated code below aims at helping you parse
 * the standard input according to the problem statement.
 **/
class Solution
{
    
    class Score
    {
        public string name;
        public int total;
        public int nbRound;
    }
    
    static void Main(string[] args)
    {
        int N = int.Parse(Console.ReadLine());
        
        List<Score> scores = new List<Score>();
        
        for (int i = 0; i < N; i++)
        {
            string PLAYER = Console.ReadLine();
            scores.Add(new Score{name=PLAYER});
        }

        for (int i = 0; i < N; i++)
        {
            string SHOOTS = Console.ReadLine();
            
            Console.Error.WriteLine(SHOOTS);             
            
            // Initialisation
            int total   = 0;
            int nbMiss  = 0;
            int nbRound = 0;
            int nbShoot = 0;
            int totalInit  = 0;
            
            string[] tabShoots = SHOOTS.Split(' ');
            foreach (string shoot in tabShoots)
            {
               string sh = shoot;
               
               // Calcul des points
               if (sh == "X") 
               {
                   nbMiss++;
                   if (nbMiss==1) {total-=20;}
                   else if (nbMiss==2) {total-=30;}
                   else if (nbMiss==3) {total=0;}
               }
               else if (sh.IndexOf("2*") != -1)
               {    nbMiss=0;
                    total+=2*int.Parse(sh.Replace("2*",""));
               }
               else if (sh.IndexOf("3*") != -1)
               {
                   nbMiss=0;
                   total+=3*int.Parse(sh.Replace("3*",""));
               }
               else 
               {
                   total+=int.Parse(sh);
                   nbMiss=0;
               }
               nbShoot++;
               
               // Analyse des résultats
               if (total<0) total=0;  
               else if (total>101) 
               {
                   nbMiss=0;
                   total=totalInit;
                   nbRound++;
                   nbShoot=0;
                   nbMiss=0;                
               }
               else if (nbShoot == 3) 
               {
                   totalInit=total;
                   nbShoot=0;
                   nbRound++;
                   nbMiss=0;                
               }

            }          
            scores[i].total=total;
            scores[i].nbRound=nbRound;
        }

        string winner = scores.Where(u => u.total == 101).OrderBy(u => u.nbRound).First().name;
        Console.WriteLine(winner);       
    }
}
