t_d, d = [], []
classifying = {"male": {}, "female": {}}

for i in range(int(input())):
    answer = input().split()

    if len(answer) == 3:
        t_d.append([int(answer[0]), answer[1], answer[2]])
    elif len(answer) == 2:
        d.append([int(answer[0]), answer[1]])

for i in t_d:
    _, gender, genre = i

    if genre not in classifying[gender]: # this line is unnecessary but rather for performance, it's to avoid looping for every single answer
        ages = [k_age for k_age, k_gender, k_genre in t_d if k_gender == gender and k_genre == genre]

        classifying[gender][genre] = range(min(ages),max(ages)+1)

for i in d:
    age, gender = i

    for genre in classifying[gender]:
        if age in classifying[gender][genre]:
            print(genre)
            break
    else: 
        print(None)
