import java.util.*;

class Solution {
    public static void main(String[] args) {
        Scanner in = new Scanner(System.in);
        int N = in.nextInt();
        int x = in.nextInt();
        int[][] queries = new int[N][2];
        int[] wall = new int[x];
        for (int i = 0; i < N; i++) {
            queries[i][0] = in.nextInt();
            queries[i][1] = in.nextInt();
            for (int j = queries[i][0]; j <= queries[i][1]; j++) {
                wall[j]++;
            }
        }
        int check = 0;
        int smol = 10000;
        for (int i = 0; i < x; i++) {
            if (wall[i] > check) check = wall[i];
            if (wall[i] < smol) smol = wall[i];
        }
        System.out.println(smol);
        System.out.println(check);
    }
}
