#include <stdio.h>

int bin(int n)
{
  int k = 0;
  int e = 1;
  int p = 1;
  int v = 0;
  for (int i = 0; ; ++i)
  {
    v += ((n >> i) & 1
             ? (k += e, 1)
             : 0) * p;
    if (k == n) break;
    p *= 10;
    e *= 2;
  }
  return v;
}

int main()
{
  int i,j,k,N;
  scanf("%d", &N);
  for (k = 0; k < N; ++k)
  {
    scanf("%d%d", &i, &j);
    printf("%d\n", bin(i) + bin(j));
  }
}
