data.solution:
allies=[]
enemies=[]
n = int(input())
m = int(input())
for j in range(n):
    row = input()
    for i in range(m):
        if row[i]=="1":
            enemies.append((i,j))
        if row[i]=="0":
            allies.append((i,j))
t=0
for enemy in enemies:
    x,y=enemy
    east,west,north,south=[False]*4
    for ally in allies:
        x0,y0=ally
        if not east:
            if x0<x and y0==y:
                east=True
                t+=1
        if not west:
            if x0>x and y0==y:
                west=True
                t+=1
        if not north:
            if y0<y and x0==x:
                north=True
                t+=1
        if not south:
            if y0>y and x0==x:
                south=True
                t+=1
print(t)
