import java.util.*;

class Solution {

    public static void main(String args[]) {
        Scanner in = new Scanner(System.in);
        String N = in.nextLine();
        String cleanString = "";
        final double GRAVITY = 9.8;
        final int INITIALVELOCITY = 158;
        final int MAXRANGE = 1800;
        final int MAXANGLE = 85;
        final String OOR = "OUT OF RANGE";

        for (int i = 0; i < N.length(); i++){
            if(Character.isDigit(N.charAt(i))){
                cleanString += N.charAt(i);
            }
        }

        int range = Integer.valueOf(cleanString);

        double radians = (Math.asin((range * GRAVITY)/(Math.pow(INITIALVELOCITY, 2))))/2;

        double angle = radians *(180/Math.PI);

        if (angle < 40){
            angle = 90 - angle;
        }

        double timeOfFlight = (2*INITIALVELOCITY*Math.sin((angle*Math.PI)/180))/GRAVITY;

        if((angle > MAXANGLE) || (range > MAXRANGE)){
            System.out.println(OOR);
        }
        else {
        System.out.format("%.1f degrees", angle); 
        System.out.println();
        System.out.format("%.1f seconds", timeOfFlight); 
        }

        in.close();
    }
}
