using System;
using System.Linq;
using System.IO;
using System.Text;
using System.Collections;
using System.Collections.Generic;

/**
 * Auto-generated code below aims at helping you parse
 * the standard input according to the problem statement.
 **/
class Solution
{
    static void Main(string[] args)
    {
        List<Place> places = new List<Place>();
        int N = int.Parse(Console.ReadLine());
        for (int i = 0; i < N; i++)
        {
            string LINE = Console.ReadLine();
            string[] chars = LINE.Split(' ');
            int j = 0;
            foreach (var c in chars)
            {
                places.Add(new Place(i,j,c.ToString()));
                j++;
            }
        }

        places = Hit(0,0,0,0,places);

        int result = places.Count(x => x.Standing);
        Console.WriteLine(result.ToString());
    }

    static List<Place> Hit(int fromx, int fromy, int x, int y, List<Place> places)
    {
        if (places.Count(c => c.X == x && c.Y == y) == 1)
        {
            Place place = places.Single(c => c.X == x && c.Y == y);
            if (place.Standing)
            {
                //initial hit (start)
                if (fromx == x && fromy == y)
                {
                    if (place.Piece == "|")
                    {
                        place.Standing = false;
                        places = Hit(x,y,x,y+1,places);
                    }
                    if (place.Piece == "-")
                    {
                        place.Standing = false;
                        places = Hit(x,y,x+1,y,places);
                    }
                    if (place.Piece == "/")
                    {
                        place.Standing = false;
                        places = Hit(x,y,x,y+1,places);
                        places = Hit(x,y,x+1,y,places);
                        places = Hit(x,y,x+1,y+1,places);
                    }
                }

                // hit from left
                if (fromx == x && fromy < y)
                {
                    if (place.Piece == "|")
                    {
                        place.Standing = false;
                        places = Hit(x,y,x,y+1,places);
                    }
                    if (place.Piece == "/")
                    {
                        place.Standing = false;
                        places = Hit(x,y,x,y+1,places);
                        places = Hit(x,y,x+1,y,places);
                        places = Hit(x,y,x+1,y+1,places);
                    }
                    if (place.Piece == "\\")
                    {
                        place.Standing = false;
                        places = Hit(x,y,x,y+1,places);
                        places = Hit(x,y,x-1,y,places);
                        places = Hit(x,y,x-1,y+1,places);
                    }
                }

                // hit from up
                if (fromx < x && fromy == y)
                {
                    if (place.Piece == "-")
                    {
                        place.Standing = false;
                        places = Hit(x,y,x+1,y,places);
                    }
                    if (place.Piece == "/")
                    {
                        place.Standing = false;
                        places = Hit(x,y,x,y+1,places);
                        places = Hit(x,y,x+1,y,places);
                        places = Hit(x,y,x+1,y+1,places);
                    }
                    if (place.Piece == "\\")
                    {
                        place.Standing = false;
                        places = Hit(x,y,x,y-1,places);
                        places = Hit(x,y,x+1,y-1,places);
                        places = Hit(x,y,x+1,y,places);
                    }
                }

                // hit from right
                if (fromx == x && fromy > y)
                {
                    if (place.Piece == "|")
                    {
                        place.Standing = false;
                        places = Hit(x,y,x,y-1,places);
                    }
                    if (place.Piece == "/")
                    {
                        place.Standing = false;
                        places = Hit(x,y,x,y-1,places);
                        places = Hit(x,y,x-1,y,places);
                        places = Hit(x,y,x-1,y-1,places);
                    }
                    if (place.Piece == "\\")
                    {
                        place.Standing = false;
                        places = Hit(x,y,x,y-1,places);
                        places = Hit(x,y,x+1,y-1,places);
                        places = Hit(x,y,x+1,y,places);
                    }
                }

                // hit from down
                if (fromx > x && fromy == y)
                {
                    if (place.Piece == "-")
                    {
                        place.Standing = false;
                        places = Hit(x,y,x-1,y,places);
                    }
                    if (place.Piece == "/")
                    {
                        place.Standing = false;
                        places = Hit(x,y,x,y-1,places);
                        places = Hit(x,y,x-1,y,places);
                        places = Hit(x,y,x-1,y-1,places);
                    }
                    if (place.Piece == "\\")
                    {
                        place.Standing = false;
                        places = Hit(x,y,x,y+1,places);
                        places = Hit(x,y,x-1,y+1,places);
                        places = Hit(x,y,x-1,y,places);
                    }
                }

                // hit from left-up
                if (fromx < x && fromy < y)
                {
                    if (place.Piece == "|")
                    {
                        place.Standing = false;
                        places = Hit(x,y,x,y+1,places);
                    }
                    if (place.Piece == "-")
                    {
                        place.Standing = false;
                        places = Hit(x,y,x+1,y,places);
                    }
                    if (place.Piece == "/")
                    {
                        place.Standing = false;
                        places = Hit(x,y,x,y+1,places);
                        places = Hit(x,y,x+1,y,places);
                        places = Hit(x,y,x+1,y+1,places);
                    }
                }

                // hit from left-down
                if (fromx > x && fromy < y)
                {
                    if (place.Piece == "|")
                    {
                        place.Standing = false;
                        places = Hit(x,y,x,y+1,places);
                    }
                    if (place.Piece == "-")
                    {
                        place.Standing = false;
                        places = Hit(x,y,x-1,y,places);
                    }
                    if (place.Piece == "\\")
                    {
                        place.Standing = false;
                        places = Hit(x,y,x,y+1,places);
                        places = Hit(x,y,x-1,y+1,places);
                        places = Hit(x,y,x-1,y,places);
                    }
                }

                // hit from right-up
                if (fromx < x && fromy > y)
                {
                    if (place.Piece == "|")
                    {
                        place.Standing = false;
                        places = Hit(x,y,x,y-1,places);
                    }
                    if (place.Piece == "-")
                    {
                        place.Standing = false;
                        places = Hit(x,y,x+1,y,places);
                    }
                    if (place.Piece == "\\")
                    {
                        place.Standing = false;
                        places = Hit(x,y,x,y-1,places);
                        places = Hit(x,y,x+1,y-1,places);
                        places = Hit(x,y,x+1,y,places);
                    }
                }

                // hit from right-down
                if (fromx > x && fromy > y)
                {
                    if (place.Piece == "|")
                    {
                        place.Standing = false;
                        places = Hit(x,y,x,y-1,places);
                    }
                    if (place.Piece == "-")
                    {
                        place.Standing = false;
                        places = Hit(x,y,x-1,y,places);
                    }
                    if (place.Piece == "/")
                    {
                        place.Standing = false;
                        places = Hit(x,y,x,y-1,places);
                        places = Hit(x,y,x-1,y,places);
                        places = Hit(x,y,x-1,y-1,places);
                    }
                }
            }
        }
        return places;
    }

}

public class Place
{
    public int X { get; set; }
    public int Y { get; set; }
    public string Piece { get; set; }
    public bool Standing { get; set; }

    public Place(int x, int y, string piece)
    {
        this.X = x;
        this.Y = y;
        this.Piece = piece;
        this.Standing = (piece != ".");
    }
}
