const round = num => num.toFixed(2).match(/-?\d+(?:\.\d?[1-9])?/)[0];

const [a, b, c] = readline().split(' ').map(v => +v);

function getZeroes(a, b, c) {
    if (a === 0) {
        if (b === 0) return [];
        return [-c / b];
    }

    const delta = b * b - 4 * a * c;
    if (delta < 0) return [];
    return [(-b - Math.sqrt(delta)) / (a * 2), (-b + Math.sqrt(delta)) / (a * 2)];
}

const result = Array.from(
    new Set(getZeroes(a, b, c))
    .add(0)
)
.sort((p, q) => p - q)
.map(x => [x, a * x * x + b * x + c])
.map(coord => '(' + coord.map(round).join(',') + ')')
.join(',');

console.log(result);
