#include <iostream>
#include <string>
#include <cmath>
using namespace std;
int main() {
    int DEC;
    cin >> DEC; cin.ignore();

    int digits = 1;  // find number of digits in answer 
    int greater = 1; // biggest number we can get using this number of digits
    int cPow = 1;
    while (greater < abs(DEC)) {
        ++digits;
        cPow *= 3;
        greater += cPow;
    }


    string sbal;
    int cDec = DEC;
    for (int i = digits - 1; i >= 0; --i) {
        greater -= cPow;
        if (abs(cDec) > greater) { // meaningful bit (1 or -1)
            sbal += (cDec >= 0 ? '1' : 'T');
            cDec -= cPow * (cDec >= 0 ? 1 : -1);
        }
        else {                     // zero bit
            sbal += '0';
        }
        cPow /= 3;
    }
    cout << sbal <<endl;
    return 0;
}
