import sys
import math

def hex2bin(hex_points) :
    h = hex_points.replace(" ", "")
    b = bin(int(h, 16))[2:]
    z = 4*len(h) - len(b)
    return b.zfill(len(b)+z)

def game(bin_points, maxi=4) :
    # normal :      maxi = 4
    # tie-break :   maxi = 7
    pts = [0, 0]
    for i, p in enumerate(bin_points) :
        pts[int(p)] += 1
        if max(pts) >= maxi :
            if abs(pts[1] - pts[0]) > 1 :
                break
            elif pts[1] == pts[0] == maxi :
                pts[0] -= 1
                pts[1] -= 1
    return pts, bin_points[i+1:]

# --------------------------------------------------
# Input...
joueurs = input().split()
bof = int(input())
hex_points = input()
# --------------------------------------------------

score = (0, 15, 30, 40, "AV", "-")
winner = -1
sets = [0, 0]
jeux = [[0], [0]]
pts = [0, 0]
bin_points = hex2bin(hex_points)
maxi = 4
tie_break = False

while bin_points :
    pts, bin_points = game(bin_points, maxi)
    #print(jeux, pts, file=sys.stderr, flush=True)
    if max(pts) >= maxi and abs(pts[1] - pts[0]) > 1 :
        j = pts[1] > pts[0]
        jeux[j][-1] += 1
        pts = [0, 0]
        if jeux[j][-1] >= 6 :
            if abs(jeux[1][-1] - jeux[0][-1]) > 1 or jeux[j][-1] == 7 :
                sets[j] += 1
                tie_break = False
                maxi = 4
                if max(sets) == (bof + 1)//2 :
                    winner = j
                    bin_points = ""
                else :
                    jeux[0] += [0]
                    jeux[1] += [0]
            elif jeux[0][-1] == jeux[1][-1] == 6 :
                tie_break = True
                if sum(sets) == bof - 1 :
                    maxi = 10
                else :
                    maxi = 7

for j in range(2) :
    print(joueurs[j].ljust(15,"."), *jeux[j], end="")
    if winner == -1 :
        if tie_break :
            print(" |",pts[j])
        else :
            print(" |", score[pts[j] + 2*(pts[1-j] == 4)])
    else :
        print()
if winner == -1 :
    print("Game in progress")
else :
    print(joueurs[winner], "wins")
