#include <iostream>
#include <string>
#include <vector>
#include <algorithm>

using namespace std;

// TEAM
class Team{
	public:
		string name;
		int points;
		int poolRanking;
		int goalAverage;
		
		Team();
};

Team::Team(){
	name = "";
	points = 0;
	poolRanking = 1;
	goalAverage = 0;
}

bool sortTiedTeams(Team A, Team B){
	if( A.points != B.points ){
		return A.points > B.points;
	}
	
	return A.goalAverage > B.goalAverage;
}

bool sortTeams(Team A, Team B){
	if( A.poolRanking != B.poolRanking ){
		return A.poolRanking < B.poolRanking;
	}
	
	if( A.points != B.points ){
		return A.points > B.points;
	}
	
	return A.goalAverage > B.goalAverage;
}

// GAME
class Game{
	public:
		string teamA;
		int scoreA;
		int triesA;
		string teamB;
		int scoreB;
		int triesB;
		
		Game();
};

Game::Game(){
	teamA = "";
	scoreA = 0;
	triesA = 0;
	teamB = "";
	scoreB = 0;
	triesB = 0;
}

// POOL
class Pool{
	public:
		Team team[4];
		Game game[12];
		
		Pool(){};
		Pool(string s);
		void addGame(string s);
		void untie(vector<Team>& t);
		void setRanking();
};

Pool::Pool(string s){
	int j = 0;
	for(int i=0; i<s.size(); i++){
		if( s[i] == ',' ){
			j++;
		}
		else{
			team[j].name += s[i];
		}
	}
}

void Pool::addGame(string s){
	// GAME NUMBER
	int g = 0;
	while( !game[g].teamA.empty() ){ g++; }
	
	int c = 0; // STRING CURSOR
	// RETRIEVING TEAM A
	for(int i=0; i<4; i++){
		if( team[i].name == s.substr(0,team[i].name.size()) ){
			game[g].teamA = team[i].name;
			c = team[i].name.size() + 1;
			break;
		}
	}
	
	// RETRIEVING SCORE A
	while( s[c] != ',' ){
		game[g].scoreA *= 10;
		game[g].scoreA += s[c] - '0';
		c++;
	}
	c++;
	
	// RETRIEVING TRIES A
	while( s[c] != ',' ){
		game[g].triesA *= 10;
		game[g].triesA += s[c] - '0';
		c++;
	}
	c++;
	
	// RETRIEVING TEAM B
	for(int i=0; i<4; i++){
		if( team[i].name.size() + c < s.size() && team[i].name == s.substr(c,team[i].name.size()) ){
			game[g].teamB = team[i].name;
			c += team[i].name.size() + 1;
			break;
		}
	}
	
	// RETRIEVING SCORE B
	while( s[c] != ',' ){
		game[g].scoreB *= 10;
		game[g].scoreB += s[c] - '0';
		c++;
	}
	c++;
	
	// RETRIEVING TRIES B
	while( c < s.size() ){
		game[g].triesB *= 10;
		game[g].triesB += s[c] - '0';
		c++;
	}
	
	for(int i=0; i<4; i++){
		if( game[g].teamA == team[i].name ){
			if( game[g].scoreA > game[g].scoreB ){
				// WIN
				team[i].points += 4;
			}
			else if( game[g].scoreA == game[g].scoreB ){
				// DRAW
				team[i].points += 2;
			}
			else if( game[g].scoreA + 7 >= game[g].scoreB ){
				// DEFENSIVE BONUS POINT
				team[i].points++;
			}
			
			if( game[g].triesA >= 4 ){
				// OFFENSIVE BONUS POINT
				team[i].points++;
			}
			
			team[i].goalAverage += game[g].scoreA - game[g].scoreB;
		}
		else if( game[g].teamB == team[i].name ){
			if( game[g].scoreB > game[g].scoreA ){
				// WIN
				team[i].points += 4;
			}
			else if( game[g].scoreB == game[g].scoreA ){
				// DRAW
				team[i].points += 2;
			}
			else if( game[g].scoreB + 7 >= game[g].scoreA ){
				// DEFENSIVE BONUS POINT
				team[i].points++;
			}
			
			if( game[g].triesB >= 4 ){
				// OFFENSIVE BONUS POINT
				team[i].points++;
			}
			
			team[i].goalAverage += game[g].scoreB - game[g].scoreA;
		}
	}
}

void Pool::untie(vector<Team>& t){
	vector<string> names;
	for(int i=0; i<t.size(); i++){
		t[i].points = 0;
		t[i].goalAverage = 0;
		names.push_back(t[i].name);
	}
	
	for(int g=0; g<12; g++){
		if( find(names.begin(), names.end(), game[g].teamA) != names.end() &&
			find(names.begin(), names.end(), game[g].teamB) != names.end() ){
				for(int i=0; i<t.size(); i++){
					if( game[g].teamA == t[i].name ){
						if( game[g].scoreA > game[g].scoreB ){
							// WIN
							t[i].points += 4;
						}
						else if( game[g].scoreA == game[g].scoreB ){
							// DRAW
							t[i].points += 2;
						}
						else if( game[g].scoreA + 7 >= game[g].scoreB ){
							// DEFENSIVE BONUS POINT
							t[i].points++;
						}
						
						if( game[g].triesA >= 4 ){
							// OFFENSIVE BONUS POINT
							t[i].points++;
						}
						
						t[i].goalAverage += game[g].scoreA - game[g].scoreB;
					}
					else if( game[g].teamB == t[i].name ){
						if( game[g].scoreB > game[g].scoreA ){
							// WIN
							t[i].points += 4;
						}
						else if( game[g].scoreB == game[g].scoreA ){
							// DRAW
							t[i].points += 2;
						}
						else if( game[g].scoreB + 7 >= game[g].scoreA ){
							// DEFENSIVE BONUS POINT
							t[i].points++;
						}
						
						if( game[g].triesB >= 4 ){
							// OFFENSIVE BONUS POINT
							t[i].points++;
						}
						
						t[i].goalAverage += game[g].scoreB - game[g].scoreA;
					}
				}
		}
	}
	
	sort(t.begin(),t.end(),sortTiedTeams);
}

void Pool::setRanking(){
	vector<int> pts;
	vector<int> tie;
	for(int i=0; i<4; i++){
		if( find(pts.begin(), pts.end(), team[i].points) != pts.end() ){
			tie.push_back(team[i].points);
		}
		pts.push_back(team[i].points);
	}
	
	sort(pts.begin(),pts.end());
	
	for(int i=0; i<4; i++){
		for(int j=3; j>=0; j--){
			if( team[i].points == pts[j] ){
				team[i].poolRanking = 4 - j;
				j = -1;
			}
		}
	}
	
	if( !tie.empty() ){
		sort(tie.begin(),tie.end());
		// IF MULTIPLE TIE POINTS IT CAN ONLY MEAN (#1 & #2) AND (#3 & #4). WE DO NOT NEED (#3 & #4)
		int tiePts = tie.back();
		vector<Team> tieTeams;
		for(int i=0; i<4; i++){
			if( team[i].points == tiePts ){
				tieTeams.push_back(team[i]);
			}
		}
		
		untie(tieTeams);
		for(int i=0; i<tieTeams.size(); i++){
			for(int j=0; j<4; j++){
				if( team[j].name == tieTeams[i].name ){
					team[j].poolRanking += i;
					j = 4;
				}
			}
		}
	}
}

// CHAMPIONS CUP
class ChampionsCup{
	public:
		Pool pool[5];
		
		ChampionsCup();
		void setGame(string s);
};

void ChampionsCup::setGame(string s){
	for(int i=0; i<5; i++){
		for(int j=0; j<4; j++){
			// FIND POOL WHERE GAME BELONGS AND ADD GAME THERE
			if( pool[i].team[j].name == s.substr(0,pool[i].team[j].name.size()) ){
				pool[i].addGame(s);
				return;
			}
		}
	}
}

ChampionsCup::ChampionsCup(){
	for(int i=0; i<5; i++){
		string s;
		getline(cin,s);
		pool[i] = Pool(s);
	}
	for(int i=0; i<60; i++){
		string s;
		getline(cin,s);
		setGame(s);
	}
	
	for(int i=0; i<5; i++){
		pool[i].setRanking();
	}
	
	vector<Team> teams;
	for(int i=0; i<5; i++){
		for(int j=0; j<4; j++){
			teams.push_back(pool[i].team[j]);
		}
	}
	
	sort(teams.begin(),teams.end(),sortTeams);
	cout << teams[0].name << " - " << teams[7].name << endl;
	cout << teams[1].name << " - " << teams[6].name << endl;
	cout << teams[2].name << " - " << teams[5].name << endl;
	cout << teams[3].name << " - " << teams[4].name << endl;
}

