using System;
using System.Collections.Generic;
using System.Linq;

namespace LetterWaves
{
    class Solution
    {
        static void Main(string[] args)
        {
            string Message = Console.ReadLine().ToLower();
            char[] Letters = Message.Where(c => char.IsLetter(c)).ToArray();
            List<int> Result = new List<int>();
            if (Letters.Length == 0)
                Console.WriteLine(0);
            else if (Letters.Length == 1)
                Console.WriteLine(1);
            else
            {
                bool Increase = Letters[1] >= Letters[0];
                int FirstIndex = 0;
                int LastIndex = 0;
                while (LastIndex < Letters.Length - 1)
                {
                    if (!Increase && Letters[LastIndex + 1] <= Letters[LastIndex]) LastIndex++;
                    else if (Increase && Letters[LastIndex + 1] >= Letters[LastIndex]) LastIndex++;
                    else
                    {
                        Increase = !Increase;
                        Result.Add(LastIndex - FirstIndex + 1);
                        FirstIndex = LastIndex + 1;
                        LastIndex = FirstIndex;
                    }
                }
                Result.Add(LastIndex - FirstIndex + 1);
                Console.WriteLine(string.Join('-', Result));
            }
        }
    }

}
