import java.util.*;
import java.util.stream.*;
import java.util.stream.Collectors;

class Solution {

    // Note: it is possible to solve this problem reading the list only once 
    // and using only one input

    public static void main(String args[]) {
        Scanner in = new Scanner(System.in);
        int oddNumber = Stream.generate(() -> in.nextInt())
            .limit(in.nextInt())
            .collect(Collectors.toMap(x -> x, x -> 1, (a, b) -> a + b))
            .entrySet().stream()
            .filter(x -> x.getValue() % 2 == 1)
            .map(x -> x.getKey())
            .findFirst().orElse(0);

        System.out.println(oddNumber);
    }
}
