import java.util.Scanner;
class Solution
{
    public static void main(String...args)
    {
        Scanner sc = new Scanner(System.in);
        int d = sc.nextInt();
        int n = sc.nextInt();

        //------------------------//
        float time[] = new float[n];
        int tid[] = new int[n];
        //------------------------//

            for(int i=0;i<n;i++)
            {
                float s = sc.nextInt();
                int tun = sc.nextInt();
                float p = sc.nextInt();
                time[i] = (d/s)+(p/2);
                tid[i] = tun;
            }

            float min = time[0];
            int pr = 0;

           for(int i=0;i<n;i++)
           {
               if(min > time[i])
               {
                   min = time[i];
                   pr = i;
               }
           }
        System.out.print(tid[pr]);
    }
}
