using System;
using System.Linq;
using System.IO;
using System.Text;
using System.Collections;
using System.Collections.Generic;

/**
 * Auto-generated code below aims at helping you parse
 * the standard input according to the problem statement.
 **/
class Solution
{
    static void Main(string[] args)
    {
        List<Place> places = new List<Place>();
        int N = int.Parse(Console.ReadLine());
        int L = int.Parse(Console.ReadLine());
        for (int i = 0; i < N; i++)
        {
            string LINE = Console.ReadLine();
            string[] chars = LINE.Split(' ');
            int j = 0;
            foreach (var c in chars)
            {
                places.Add(new Place(i,j,(c == "C") ? L : 0));
                j++;
            }
        }

        places = Start(places, N-1,L);

        int result = places.Count(x => x.Light == 0);
        Console.WriteLine(result.ToString());
    }

    static List<Place> Start (List<Place> places, int size, int candleLight)
    {
        foreach (var p in places.Where(z => z.Light == candleLight))
        {
            places = Light(p.X, p.Y, places, p.Light, size);
        }
        return places;
    }

    static List<Place> Light (int x, int y, List<Place> places, int l, int size)
    {
        if (places.Single(z => z.X == x && z.Y == y).Light <= l)
        {
            places.Single(z => z.X == x && z.Y == y).Light = l;
            int light = l-1;

            if (light > 0)
            {
                if (x > 0 && y > 0)
                {
                    places = Light(x-1,y-1,places,light, size);
                }
                if (x > 0 && y < size)
                {
                    places = Light(x-1,y+1,places,light, size);
                }
                if (x > 0)
                {
                    places = Light(x-1,y,places,light, size);
                }

                if (y > 0)
                {
                    places = Light(x,y-1,places,light, size);
                }

                if (y < size)
                {
                    places = Light(x,y+1,places,light, size);
                }

                if (x < size && y > 0)
                {
                    places = Light(x+1,y-1,places,light, size);
                }
                if (x < size && y < size)
                {
                    places = Light(x+1,y+1,places,light, size);
                }
                if (x < size)
                {
                    places = Light(x+1,y,places,light, size);
                }
            }
        }
        return places;
    }

}

public class Place
{
    public int X { get; set; }
    public int Y { get; set; }
    public int Light { get; set; }

    public Place(int x, int y, int light)
    {
        this.X = x;
        this.Y = y;
        this.Light = light;
    }
}
