import java.util.ArrayList;
import java.util.List;
import java.util.Scanner;

public class Solution {
	public static void main(String args[]) {
		Scanner in = new Scanner(System.in);
		int N = in.nextInt();
		if (in.hasNextLine()) {
			in.nextLine();
		}
		String TEXT = in.nextLine();
		for (String line : getLines(TEXT, N))
			System.out.println(line);
	}

	public static List<String> getLines(String message, int length) {

		List<String> result = new ArrayList<>();

		int pos = 0;

		do {
			String tmp = message.substring(pos % message.length()) + message.substring(0, pos % message.length());
			tmp = tmp.substring(0, Math.min(length, message.length()));

			pos++;
			result.add(tmp);
		} while (pos + length <= message.length());
		return result;
	}
}
