d = int(input())
g, e, a = map(int, input().split())
ans = ""
for i in range(d):
  if g <= 0:
    ans = "Dead"
    break
  else:
    g -= e
    if g <= 0:
      ans = "Dead"
      break
    else:
      for i in range(a):
        g += 1
      ans = "alive"
if g <= 0:
    print("Dead")
else:
    print(g)
