use std::io;

fn main() {
    let mut input = String::new();
    io::stdin().read_line(&mut input).unwrap();

    let n: i32 = input.trim().parse().unwrap();

    for _ in 0..n {
        let mut x = String::new();
        io::stdin().read_line(&mut x).unwrap();
        x = format!("{:0>2}", x.trim());

        if let Ok(v) = x[x.len() - 2..].parse::<i32>() {
            println!("{}", v % 4 == 0);
        } else if x.chars().last().unwrap_or('4').to_digit(10).unwrap_or(4) % 2 == 1 {
            println!("false");
        } else {
            println!("idk");
        }
    }
}
