const n = parseInt(readline());
var col=[];
for (let i = 0; i < n; i++) {
    var inputs = readline().split(' ');
    const colorName = inputs[0];
    const RGB = inputs[1];
    var R=+("0x"+RGB.slice(1,3));
    var G=+("0x"+RGB.slice(3,5));
    var B=+("0x"+RGB.slice(5,7));
    col.push({name:colorName, L:0.299*R + 0.587*G + 0.114*B})
}
col.sort((a,b)=>a.L-b.L)
console.error(col)
for (let i = 0; i < n; i++) {
    console.log(col[i].name);
}
