import java.util.*;

class Solution {

    public static void main(String args[]) {

        Scanner in = new Scanner(System.in);

        List<String> foods = new ArrayList<String>();
        List<String> recipes = new ArrayList<String>();


        List<String> nonRegularString = new ArrayList<String>();

        String accentChar = "àáâãäëêéèìîíïñòóôõöùúûüýÿÃÁÂÀÄÉÈÊËÏÌÍÎÑÕÖÔÒÓÚÜÛÙÝŸ";
        String regulrChar = "aaaaaeeeeiiiinooooouuuuyyAAAAAEEEEIIIINOOOOOUUUUYY";

        int N = in.nextInt();

        if (in.hasNextLine()) {
            in.nextLine();
        }

        for (int i = 0; i < N; i++) {
            String availableFood = in.nextLine();
            foods.add(availableFood);

            for(int j = 0; j < accentChar.length(); j++){

                char currentChar = accentChar.charAt(j);

                if(availableFood.indexOf(currentChar) > -1){
                    nonRegularString.add(availableFood);
                    break;
                }
            }
        }

        int P = in.nextInt();
        if (in.hasNextLine()) {
            in.nextLine();
        }

        for (int i = 0; i < P; i++) {
            String[] recipe = in.nextLine().split(",");

            for(int j = 0; j < accentChar.length(); j++){

                char currentChar = accentChar.charAt(j);

                if(String.join(",", recipe).indexOf(currentChar) > -1){
                    nonRegularString.add(String.join(",", recipe));
                    break;
                }
            }            

            boolean canBeCooked = true;

            for(int j = 1; j < recipe.length; j++){
                canBeCooked = canBeCooked && foods.contains(recipe[j]);
            }

            if(canBeCooked) recipes.add(recipe[0]);             
        }

        if (recipes.size() > 0){
            System.out.print(String.join("\n", recipes.toArray(new String[0])));
        }else{
            System.out.println("FASTFOOD JOKER");
        }
        System.err.println(" ");
        for(String nRegul : nonRegularString){
            System.err.println(nRegul);
        }        
    }   
}
