import java.util.*;
import java.io.*;
import java.math.*;

class Solution {

    public static void main(String args[]) {
        Scanner in = new Scanner(System.in);
        int a = in.nextInt();
        int b = in.nextInt();
        if (in.hasNextLine()) {
            in.nextLine();
        }
        String s = in.nextLine();

        int chars = s.replaceAll("[^a-zA-Z0-9]", "").length();

        if(a*b>=chars) {
            System.out.println((int) Math.ceil((double)chars/b) + " Enough");
        } else {
            System.out.println((int) Math.ceil((double)chars/b) + " Not enough");
        }
    }
}

