let R = Number(readline()),   /// rows
    C = Number(readline()),   /// cols
    T = Number(readline());   /// threshold

const VISITED = "V";

// get sum of all digits in the number provided (x >= 0)
const sumDigits = (x) => x.toString().split('').map(Number).reduce((sum, c) => sum += c, 0);

// initialize an RxC grid with the sum of the digits of its R & C index
let grid = [...Array(R)].map((_, r) => [...Array(C)].map((_, c) => sumDigits(r) + sumDigits(c)));

const visit = (row, col, t) => {

    // out of bounds or visited
    if (row >= grid.length || col >= grid[row].length || grid[row][col] === VISITED) {
        return 0;
    }

    // threshold exceeded
    if (grid[row][col] > t) {
        return 0;
    }

    // mark this cell visited
    grid[row][col] = VISITED;

    // go right, go down and +1 for this cell
    return visit(row, col + 1, T) + visit(row + 1, col, T) + 1;
}


console.log(visit(0, 0, T));
