def addMinute(time):	# format HHMM
	t=(int(time[:2])*60+int(time[2:])+1)%1440
	return '{:02d}{:02d}'.format(t//60,t%60)

def calculate(time1,time2):
	v = 0
	for i,j in zip(time1,time2):
		if j>i:
			v += int('452331541-'[int(i)])
		elif j<i:
			v += int('-433432312'[int(i)])
	return v

for i in range(int(input())):
	time = input().replace(':','')
	print(calculate(time,addMinute(time)))
