using System;
using System.Linq;
using System.IO;
using System.Text;
using System.Collections;
using System.Collections.Generic;

/**
 * Auto-generated code below aims at helping you parse
 * the standard input according to the problem statement.
 **/
class Solution
{
    static void Main(string[] args)
    {
        List<Fruit> fruits = new List<Fruit>();
        int N = int.Parse(Console.ReadLine());
        for (int i = 0; i < N; i++)
        {
            string fs = Console.ReadLine();
            string[] chars = fs.Split(' ');
            fruits.Add(new Fruit(chars[0], Convert.ToInt32(chars[1])));
        }
        string LINE = Console.ReadLine();

        foreach (var c in LINE)
        {
            fruits.Single(x => x.Name == c.ToString()).Add();
        }

        Console.WriteLine(fruits.Sum(x => x.ToJuice()).ToString());
    }
}

public class Fruit
{
    public string Name {get;}
    public int ForJuice {get;}
    public int Pieces {get; set;}

    public Fruit (string n, int fj)
    {
        this.Name = n;
        this.ForJuice = fj;
        this.Pieces = 0;
    }

    public void Add()
    {
        this.Pieces++;
    }

    public int ToJuice()
    {
        return (this.Pieces / this.ForJuice);
    }
}
