import java.util.*;
import java.io.*;
import java.math.*;

/**
 * Auto-generated code below aims at helping you parse
 * the standard input according to the problem statement.
 **/
class Solution {

    public static void main(String args[]) {
        Scanner in = new Scanner(System.in);
        int N = in.nextInt();

        int optimalPath = 0;
        int minimumCasualty = 0;
        int temp = 0;

        for (int i = 0; i < N; i++) {
            temp = in.nextInt()*in.nextInt();
            if (i == 0 || minimumCasualty > temp){
                minimumCasualty = temp;
                optimalPath = i+1;
            }
        }

        System.out.println(optimalPath);
    }
}
