#include <iostream>
using namespace std;
 
string encode(char *s) {
	string a="",b="";
	bool f=true;
	for (int i=0;s[i];i++) {
		if (s[i]!=' ')
			if (f) {
				a+=s[i]; f=false;
			} else {
				b+=s[i];
			}
		else {
			b+=s[i]; f=true;
		}
	}
	return a+' '+b;
}
 
string decode(const string s) {
	string a="",b="";
	int i=0,j=0; while (s[j]!=' ') j++; j++;
	while (s[j]) {
		if (s[j]>='A')
			b+=s[j++];
		else {
			a+=s[i++]+b+s[j++];
			b="";
		}
	}
	a+=s[i]+b;
	return a;
}
 

int main() {
    string s;
	getline(cin,s);
	int d=s.length();
	string t = decode(s);
	cout << t;
	return 0;
}
