if(d<0): print('No real solutions')
else:
    if (-b+d**.5)%(2*a)==0:
        a1=int((-b+d**.5))//(2*a)
    else: a1=(-b+d**.5)/(2*a)
    if int((-b-d**.5))%(2*a)==0:
        a2=int((-b-d**.5))//(2*a)
    else: a2=(-b-d**.5)/(2*a)

    if a1==a2:print(a1)
