n,w=map(int,input().split())
l=[input()for i in"."*n]
y=x=i=0
for d,e in enumerate(l):v=e.find("P");y,x=(d,y)[v<0],max(v,x)
T={(y,x)}
while"T"not in[l[q][w]for(q,w)in T]:
 A=T
 for a,b in T:T=T|{(min(a+1,n-1),b),(max(a-1,0),b),(a,min(b+1,w-1)),(a,max(b-1,0))}
 T={*[(q,w)for(q,w)in T if l[q][w]!="."]}
 if T==A:break
 i+=1
print((f"hurray!! {i}","arghhh!")[A==T])
