def canSwap( g, row1, col1, row2, col2 ):
    if row1>8 or col1>8 or row2>8 or col2>8:
        return False
    result=False
    t=g[row1][col1]
    g[row1][col1]=g[row2][col2]
    g[row2][col2]=t
    s=''.join(g[row1][max(col1-2,0):min(9,col1+3)])
    if g[row1][col1]*3 in s:
        result=True
    s=''.join(g[r][col1] for r in range(max(row1-2,0),1+min(8,row1+2)))
    if g[row1][col1]*3 in s:
        result=True
    s=''.join(g[row2][max(col2-2,0):min(9,col2+3)])
    if g[row2][col2]*3 in s:
        result=True
    s=''.join(g[r][col2] for r in range(max(row2-2,0),1+min(8,row2+2)))
    if g[row2][col2]*3 in s:
        result=True
    t=g[row1][col1]
    g[row1][col1]=g[row2][col2]
    g[row2][col2]=t
    return result

g=[]
for i in range(9):
