const totalCows = parseInt(readline());
const width = parseInt(readline());
const height = parseInt(readline());
const field = [];

for (let i = 0; i < height; i++) {
    field.push(readline().split(''));
}

var cows = 0;
var result = -1;

for(let x=0 ; x < width ; x++ ){
    for(let y=0 ; y < height ; y++){
        cows += (field[y][x] == '#')?1:0 ;
    }
    if (cows == totalCows/2){
        result = x+1 ;
        break;
    }
}

print(result)
