using System;
using System.Linq;
using System.IO;
using System.Text;
using System.Collections;
using System.Collections.Generic;

class Solution
{
    static void Main(string[] args)
    {
        string recipe = Console.ReadLine();
        string[] allCodedLines = recipe.Split("nl");

        foreach (string line in allCodedLines)
        {
            string s = ConvertRecipeLineToImage(line.Trim());
            Console.WriteLine(s);
        }   
    }

    public static string ConvertRecipeLineToImage(string s)
    {
        if (s.Length < 1) return "";
        string lineImage = "";

        string[] Ray = s.Split(" ");
        foreach (string chunk in Ray)
        {            
            string[] sepArray = SeparateNumberFromChar(chunk).Split(' ');

            int i = Convert.ToInt16(sepArray[0]);
            string strPart = sepArray[1];
            int strLen = strPart.Length;

            if (strLen == 1) //normal char
            {
                string series = new String(Convert.ToChar(strPart),i); 
                lineImage = lineImage + series;
            }
            else //its special like \ or ' or space
            {
                char ch = ' ';
                if (strPart == "sQ") ch = '\'';
                if (strPart == "bS") ch = '\\';
                if (strPart == "sp") ch = ' ';
                string series = new String(ch,i); 
                lineImage = lineImage + series;
            }
        }
        return lineImage;
    }

    public static string SeparateNumberFromChar(string s)
    {
        string numberPart = "";
        string charPart = "";

        foreach (char ch in s)
        {
            if (Char.IsNumber(ch))
            {
                numberPart += ch;
            }
            else
            {
                charPart += ch;
            }
        }

        //There could be:
        //  31     to mean 111
        //  or
        //  108    to mean 8888888888 
        if (charPart.Length == 0)
        {
            int len = s.Length;
            int lenOfNum = len - 1;
            int startRight = len - 1;

            string a = s.Substring(0,lenOfNum);
            string b = s.Substring(startRight,1);
            return (a + " " + b);
        }
        return numberPart + " " + charPart;
    }
}

