import java.text.DecimalFormat;
import java.util.Scanner;

class Solution {

    public static void main(String args[]) {
        DecimalFormat df = new DecimalFormat("0.00"); 
        Scanner in = new Scanner(System.in);
        
        int x1 = in.nextInt();
        int y1 = in.nextInt();
        int r1 = in.nextInt();
        int x2 = in.nextInt();
        int y2 = in.nextInt();
        int r2 = in.nextInt();
        
        int squaredR1 = r1 * r1;
        int squaredR2 = r2 * r2;

        double d = dist(x1, y1, x2, y2);

        if (Math.sqrt(d) >= r1 + r2) {
            System.out.println(df.format(0));
        } else if (Math.sqrt(d) <= Math.abs(r1 - r2)) {
            int minR = Math.min(r1, r2);
            System.out.println(df.format(Math.PI * minR * minR));
        } else {
            int nbTestPoint = 100000000;
            double nbTestPointPerDim = Math.sqrt(nbTestPoint);
    
            int minX = Math.max(x1 - r1, x2 - r2);
            int maxX = Math.min(x1 + r1, x2 + r2);
            double dx = (maxX - minX) / nbTestPointPerDim;
    
            int minY = Math.max(y1 - r1, y2 - r2);
            int maxY = Math.min(y1 + r1, y2 + r2);
            double dy = (maxY - minY) / nbTestPointPerDim;
    
            int intersectPoints = 0;
            for (double x = minX + dx / 2; x <= maxX; x += dx) {
                for (double y = minY + dy / 2; y <= maxY; y += dy) {
                    if (dist(x, y, x1, y1) < squaredR1 && dist(x, y, x2, y2) < squaredR2) {
                        ++intersectPoints;
                    }
                }
            }
    
            double ratioIntersectPoints = intersectPoints * 1.0 / nbTestPoint;
            int totalArea = (maxX - minX) * (maxY - minY);
            System.out.println(df.format(ratioIntersectPoints * totalArea));
        }
    }
    
    public static double dist(double x1, double y1, int x2, int y2) {
        return (x2 - x1) * (x2 - x1) + (y2 - y1) * (y2 - y1);
    }
}
