import sys
import math

def rom2ara(nombre) :
    symbole = nombre[-1]
    rg = symboles.index(symbole)
    compt = 0
    special = False
    negatif = 0
    for i in range(len(nombre)) :
        c = nombre[-1-i]
        if c == symbole :
            compt += 1
        elif symboles.index(c) == rg - 1 :
            special = True
            negatif = rg - 1
        elif symboles.index(c) == rg - 2 :
            special = True
            negatif = rg - 2
        else :
            i -= 1
            break
    calcul = compt * valeurs[rg] - (special)*valeurs[negatif]
    if i == len(nombre) - 1 :
        return calcul
    else :
        total =  calcul + rom2ara(nombre[:-1-i])
        return total

def ara2rom(nombre) :
    rom = ""
    n = nombre[::-1]
    for i, c in enumerate(n) :
        conversion = convertir(c, i)
        rom = conversion + rom
    return rom

def convertir(chiffre, rang) :
    symb = "IXCM"
    symb5 = "VLD"
    if chiffre == "0" :
        return ""
    else :
        if (int(chiffre) < 4) or (chiffre == "4" and rang == 3) :
            return symb[rang] * int(chiffre)
        if chiffre == "4" :
            return symb[rang] + symb5[rang]
        if chiffre == "9" :
            return symb[rang] + symb[rang+1]
        else  :
            return symb5[rang] + symb[rang] * (int(chiffre) - 5)

symboles = "IVXLCDM"
valeurs = (1, 5, 10, 50, 100, 500, 1000)
rom1 = input()
rom2 = input()
r1 = rom2ara(rom1)
r2 = rom2ara(rom2)
r3 = str(int(r1) + int(r2))
rom3 = ara2rom(r3)
print(rom3)
