import java.util.Scanner;

public class Solution {
	public static void main(String[] args) {
	   Scanner in = new Scanner(System.in);
	   in.nextLine();
	   String[] vals = in.nextLine().split(" ");
	   String str = "";
	   
	   for(int i=0; i<vals.length; i++) {
	      String strVal = vals[i];
	      
	      long numVal = 0;
	      try {
	         numVal = Long.parseLong(strVal);
	      } catch (Exception e) {}
	      
	      str += (i != 0) ? " " : "";
	      str += (numVal > 0) ? nToA(numVal) : aToN(strVal);
	   }
	   System.out.println(str);
	   in.close();
	}
	
	public static String nToA(long n) {
	   String str = "";
      while(n > 0) {
         str = (char)("A".charAt(0) + ((n - 1) % 26)) + str;
         n = (n - 1) / 26;
      }
      return str;
	}
	
	public static long aToN(String str) {
	   long n = str.charAt(0) - "A".charAt(0);
	   for(int i=1; i<str.length(); i++) {
	      n = (n + 1) * 26;
	      n += str.charAt(i) - "A".charAt(0);
	   }
	   return n+1;
	}
}
