#include <iostream>
#include <string>
#include <vector>
#include <algorithm>
#define d double
using namespace std;
d race(d l, d h, d r) {
    return hypot(h,r>l-r?r:l-r);
}

int main()
{
    double l,h,r;
    cin >> l >> r >> h;
    double ans = race(l,h,r);
    cout << ans << endl;
    return 0;
}
