import sys
import math

# Auto-generated code below aims at helping you parse
# the standard input according to the problem statement.

bank_cards = input().split(" ")
player_cards = input().split(" ")

def find_score(cards):
    if len(cards)==2:
        if cards[0]=='A' and cards[1] in "10JQK" or cards[1]=="A" and cards[0] in "10JQK":
            return True,21
    score = 0
    number_ace = 0
    for card in cards:
        if card in "10JQK":
            score+=10
        if card in "23456789":
            score+=int(card)
        if card=="A":
            number_ace += 1
    for _ in range(number_ace):
        if score<10:
            score+=11
    return False, score

bank_bj, bank_score = find_score(bank_cards)
player_bj, player_score = find_score(player_cards)

if player_bj:
    if bank_bj:
        result = "Draw"
    else:
        result = "Blackjack!"
elif bank_bj:
    result = "Bank"
elif player_score>21:
    result = "Bank"
elif bank_score>21 and player_score<=21:
    result="Player"
else:
    if bank_score>player_score:
        result = "Bank"
    elif bank_score<player_score:
        result = "Player"
    else:
        result = "Draw"


print(result)
