import java.util.*;
import java.io.*;
import java.math.*;

class Solution {

    // Dimensions of the matrix
    private final int WIDTH;
	private final int HEIGHT;

	// Representation of the tiles
	private boolean matrix[][];
	// Identification of the tiles reachable from (0,0)
	private boolean reachable[][];

    // Constructor taking dimensions of the matrix as input
    public Solution(int WIDTH, int HEIGHT) {

        // Initialise the fields
        this.WIDTH = WIDTH;
        this.HEIGHT = HEIGHT;
        this.matrix = new boolean[WIDTH][HEIGHT];
        this.reachable = new boolean[WIDTH][HEIGHT];

        // starting tiles and final tiles are already present.
        matrix[0][0] = true;
        reachable[0][0] = true;
        matrix[WIDTH-1][HEIGHT-1] = true;
    }

    // Add a new tile and return true if it created a path.
	private boolean add(int tileX, int tileY) {

		matrix[tileX][tileY] = true;

		// Is there any reachable point around ?
		// If yes, then the new tile is reachable as well.
		boolean newTileReachable = false;
		if (tileX > 0) {
			if (reachable[tileX-1][tileY]) {
				newTileReachable = true;
			}
		}
		if (!newTileReachable && tileX < WIDTH -1) {
			if (reachable[tileX+1][tileY]) {
				newTileReachable = true;
			}
		}
		if (!newTileReachable && tileY > 0) {
			if (reachable[tileX][tileY-1]) {
				newTileReachable = true;
			}
		}
		if (!newTileReachable && tileY < HEIGHT -1) {
			if (reachable[tileX][tileY+1]) {
				newTileReachable = true;
			}
		}

		// If point is reachable, we need to explore from there for new reachable tiles
		if (newTileReachable)
			return exploreFrom(tileX,tileY);
		else
			return false;

	}

	// Recursive function which search for new reachable tiles from the starting point.
	// Assumption is that tileX, tileY is reachable.
	// Return true if we reached the final position.
	private boolean exploreFrom(int tileX, int tileY) {

        // Mark the tile as reachable from (0,0)
		reachable[tileX][tileY] = true;

		// Check if we reached the final position.
		if (tileX == WIDTH-1 && tileY == HEIGHT-1) {
			return true;
		}

		// Is there any tile around not reachable before ?
		if (tileX > 0) {
			if (matrix[tileX-1][tileY] && !reachable[tileX-1][tileY]) {
				if (exploreFrom(tileX-1,tileY)) {
					return true;
				}
			}
		}
		if (tileX < WIDTH -1) {
			if (matrix[tileX+1][tileY] && !reachable[tileX+1][tileY]) {
				if (exploreFrom(tileX+1,tileY)) {
					return true;
				}
			}
		}
		if (tileY > 0) {
			if (matrix[tileX][tileY-1] && !reachable[tileX][tileY-1]) {
				if (exploreFrom(tileX, tileY-1)) {
					return true;
				}
			}
		}
		if (tileY < HEIGHT -1) {
			if (matrix[tileX][tileY+1] && !reachable[tileX][tileY+1]) {
				if (exploreFrom(tileX,tileY+1)) {
					return true;
				}
			}
		}

		// No path found
		return false;
	}

    public static void main(String args[]) {
        Scanner in = new Scanner(System.in);

        // Get size of the matrix        
        final int WIDTH = in.nextInt();
        final int HEIGHT = in.nextInt();
		final int K = in.nextInt();

        Solution solution = new Solution(WIDTH, HEIGHT);

        int runNumber = 1;
        while (true) {

            // Get coordinates of a new tile
            int tileX = in.nextInt();
            int tileY = in.nextInt();

            // Iterate until a path exists
            if (solution.add(tileX, tileY)) {
                System.out.println(runNumber);
                return;
            } else {
                runNumber++;
            }            

        }

    }
}
