import sys
import math

def chercher(a) :
    for y in range(h-1) :
        for x in range(w-1) :
            max_c = min(h - y, w - x)
            for c in range(1, max_c) :
                ok = False
                if lignes[y][x] == a or lignes[y][x] == "?" :
                    liste = lignes[y][x : x + c + 1]
                    liste += lignes[y + c][x : x + c + 1]
                    for k in range(1, c) :
                        liste += lignes[y + k][x] + lignes[y + k][x + c]
                    if liste.count(a) == nb_chiffres[int(a)] :
                        ok = True
                        for t in liste :
                            if t != a and t != "?" :
                                ok = False
                                break
                        if ok :
                            return c + 1
    return 0


h, w = map(int, input().split())
nb = int(input())
lignes = [""] * h
for i in range(h) :
    lignes[i] = input()

a_trouver = [str(i + 1) for i in range(nb)]
nb_chiffres = [0 for _ in range(nb + 1)]
for i in range(1, nb + 1) :
    nb_c = 0
    for k in range(h) :
        nb_c += lignes[k].count(str(i))
    nb_chiffres[i] = nb_c
carres = []

while len(a_trouver) != 0 :
    trouve = False
    for a in a_trouver :
        cote = chercher(a)
        if cote > 1 :
            carres += [[a, cote]]
            for k in range(h) :
                lignes[k] = lignes[k].replace(a, "?")
            a_trouver.remove(a)
            break

for i in range(nb) :
    print(carres[nb - i - 1][0], carres[nb - i - 1][1])
