#include <stdlib.h>
#include <stdio.h>
#include <string.h>

typedef struct Guess {
    char guess[5];
    int bulls;
    int cows;
} Guess;

int meets_criteria(char *secret, Guess guess) {
    int bull[4] = {0};
    int bulls = 0;
    int cow[4] = {0};
    int cows = 0;
    for (int i = 0; i < 4; i++) {
        if (guess.guess[i] == secret[i]) {
            bull[i] = 1;
            bulls++;
        }
    }
    for (int i = 0; i < 4; i++) {
        if (!bull[i]) {
            for (int j = 0; j < 4; j++) {
                if (i != j && !bull[j] && !cow[j] && guess.guess[i] == secret[j]) {
                    cow[j] = 1;
                    cows++;
                }
            }
        }
    }

    return bulls == guess.bulls && cows == guess.cows;
}

int main()
{
    int n;
    scanf("%i", &n);
    Guess *guesses = (Guess *) malloc(n * sizeof(Guess));

    for (int i = 0; i < n; i++) {
        scanf("%s %d %d", 
              guesses[i].guess, 
              &(guesses[i].bulls), 
              &(guesses[i].cows));
    }

    char secret[5] = {0};

    // brute force
    for (secret[0] = '0'; secret[0] <= '9'; secret[0]++) {
    for (secret[1] = '0'; secret[1] <= '9'; secret[1]++) {
    for (secret[2] = '0'; secret[2] <= '9'; secret[2]++) {
    for (secret[3] = '0'; secret[3] <= '9'; secret[3]++) {
        int i = 0;
        int pass = 1;
        while (i < n && pass) {
            pass = meets_criteria(secret, guesses[i]);
            i++;
        }
        if (pass) printf("%s\n", secret);
    }
    }
    }
    }

    free(guesses);

    return 0;
}
