const v = readline();
const i = readline();

const n = v.split('.')
const increments = { MAJOR: 0, MINOR: 1, PATCH: 2 }
const indexToChange = increments[i];
const incremented = +n[increments[i]] + 1

for (let i = indexToChange; i <= 2; i++){
    n.splice(i, 1, 0)
}
n.splice(indexToChange, 1, incremented)

