let start = parseInt(readline());
const n = parseInt(readline());
let can_push = true
for (let i =0;i < n;i++){
    let operation = readline()
    if (operation == "PUSH"){
        if (start >= 3){
            start -= 3;
        }else{
            can_push = false;
        }
    }else
        start++;
}
console.log(can_push ? start : "Go evaluate")
