public enum CardValue
{
    Two = 2,
    Three = 3,
    Four = 4,
    Five = 5,
    Six = 6,
    Seven = 7,
    Eight = 8,
    Nine = 9,
    Ten = 10,
    Jack = 11,
    Queen = 12,
    King = 13,
    Ace = 14
}

public enum Suit
{
    C,
    D,
    H,
    S
}

public enum HandType
{
    HIGH_CARD,
    PAIR,
    TWO_PAIR,
    THREE_OF_A_KIND,
    STRAIGHT,
    FLUSH,
    FULL_HOUSE,
    FOUR_OF_A_KIND,
    STRAIGHT_FLUSH,
    ROYAL_FLUSH,
}

public class Card
{
    private CardValue value;
    private Suit suit;

    public Card(string rawData)
    {
        this.value = this.ParseCardValue(rawData);
        this.suit = this.ParseSuit(rawData);
    }

    public Card(CardValue value, Suit suit)
    {
        this.value = value;
        this.suit = suit;
    }

    public Suit Suit { get { return this.suit; } }
    public CardValue Value { get { return this.value; } }

    public static List<Card> ParseCards(List<string> cards)
    {
        return cards.Select(i => new Card(i)).ToList();
    }

    public override string ToString()
    {
        return this.value.ToString() + this.suit.ToString();
    }

    public string OutputValue()
    {
        return (int)this.value < 10 ? ((int)this.value).ToString() :
            (int)this.value < 11 ? "T" :
            (int)this.value < 12 ? "J" :
            (int)this.value < 13 ? "Q" :
            (int)this.value < 14 ? "K" :
            "A";
    }

    private CardValue ParseCardValue(string rawValue)
    {
        string valuestring = rawValue.Substring(0, rawValue.Length - 1);

        return valuestring == "T" ? CardValue.Ten :
            valuestring == "J" ? CardValue.Jack :
            valuestring == "Q" ? CardValue.Queen :
            valuestring == "K" ? CardValue.King :
            valuestring == "A" ? CardValue.Ace :
            (CardValue)int.Parse(valuestring);
    }

    private Suit ParseSuit(string rawData)
    {
        return (Suit)Enum.Parse(typeof(Suit), rawData.Substring(rawData.Length - 1));
    }
}

public class FiveCardHand
{
    public HandType handType;
    public List<Card> cards;

    public FiveCardHand(List<Card> cards)
    {
        this.cards = cards;
        this.CalculateHand();
    }

    public List<Card> Cards { get { return this.cards; } }

    public HandType HandType { get { return this.handType; } }

    public override string ToString()
    {
        var sb = new StringBuilder();

        foreach (var card in this.cards)
        {
            if (sb.Length > 0)
            {
                sb.Append(" ");
            }

            sb.Append(card.ToString());
        }

        return sb.ToString();
    }

    public string OutputValues()
    {
        var sb = new StringBuilder();

        foreach (var card in this.cards)
        {
            sb.Append(card.OutputValue());
        }

        return sb.ToString();
    }

    public bool IsDrawnWithHand(FiveCardHand otherHand)
    {
        if (otherHand.HandType > this.HandType)
        {
            return false;
        }

        if (this.HandType > otherHand.HandType)
        {
            return false;
        }

        return this.OutputValues() == otherHand.OutputValues();
    }

    public bool IsGreaterThanOrEqualTo(FiveCardHand otherHand)
    {
        if (this.IsDrawnWithHand(otherHand))
        {
            return true;
        }

        if (this.HandType < otherHand.HandType)
        {
            return false;
        }

        if (this.HandType > otherHand.HandType)
        {
            return true;
        }

        // Iterate ordered cards, first one that's different tells us the result
        for (int i = 0; i < this.cards.Count(); ++i)
        {
            var thisCardValue = this.cards[i].Value;
            var otherCardValue = otherHand.Cards[i].Value;

            if (thisCardValue != otherCardValue)
            {
                return thisCardValue > otherCardValue;
            }
        }

        // Should never get to here
        throw new Exception("FiveCardHand.IsGreaterThanOrEqualTo: Could not determine equivalence.");
    }

    private void CalculateHand()
    {
        this.cards = this.cards.OrderByDescending(i => i.Value).ToList();

        if (this.IsRoyalFlush())
        {
            this.handType = HandType.ROYAL_FLUSH;
        }
        else if (this.IsStraightFlush())
        {
            this.handType = HandType.STRAIGHT_FLUSH;
            this.ReorderStraightOrStraightFlush();
        }
        else if (this.IsFourOfAKind())
        {
            this.handType = HandType.FOUR_OF_A_KIND;
            this.ReorderFourOfAKindOrFullHouse();
        }
        else if (this.IsFullHouse())
        {
            this.handType = HandType.FULL_HOUSE;
            this.ReorderFourOfAKindOrFullHouse();
        }
        else if (this.IsFlush())
        {
            this.handType = HandType.FLUSH;
        }
        else if (this.IsStraight())
        {
            this.handType = HandType.STRAIGHT;
            this.ReorderStraightOrStraightFlush();
        }
        else if (this.IsThreeOfAKind())
        {
            this.handType = HandType.THREE_OF_A_KIND;
            this.ReorderThreeOfAKind();
        }
        else if (this.IsTwoPair())
        {
            this.handType = HandType.TWO_PAIR;
            this.ReorderOneOrTwoPair();
        }
        else if (this.IsPair())
        {
            this.handType = HandType.PAIR;
            this.ReorderOneOrTwoPair();
        }
        else
        {
            this.handType = HandType.HIGH_CARD;
        }
    }

    private void ReorderOneOrTwoPair()
    {
        var significantValues = this.cards
            .Select(i => new { Value = i.Value, Count = this.cards.Count(j => j.Value == i.Value) })
            .Where(i => i.Count > 1)
            .Select(i => i.Value);
        var significantCards = this.cards.Where(i => significantValues.Contains(i.Value)).OrderByDescending(i => i.Value).ToList();
        var kickers = this.cards.Where(i => !significantValues.Contains(i.Value)).OrderByDescending(i => i.Value).ToList();
        this.SetCardsBasedOnOrderedSignificantCardsAndKickers(significantCards, kickers);
    }

    private void ReorderThreeOfAKind()
    {
        // Middle card in ordered list must be of the significant value e.g. 5 in 76555 or 55532
        var significantValue = this.cards[2].Value;
        var significantCards = this.cards.Where(i => i.Value == significantValue).ToList();
        var kickers = this.cards.Except(significantCards).OrderByDescending(i => i.Value).ToList();
        this.SetCardsBasedOnOrderedSignificantCardsAndKickers(significantCards, kickers);
    }

    private void ReorderFourOfAKindOrFullHouse()
    {
        // Middle card in ordered list must be of the significant value e.g. 5 in 65555 or 55533
        var significantValue = this.cards[2].Value;

        if (this.cards.Any(i => i.Value > significantValue))
        {
            this.cards = this.cards.OrderBy(i => (int)i.Value).ToList();
        }
    }

    private void ReorderStraightOrStraightFlush()
    {
        // Reorder if Ace is low
        if (this.cards.Any(i => i.Value == CardValue.Ace) && this.cards.Any(i => i.Value == CardValue.Five))
        {
            var ace = this.cards[0];
            this.cards.RemoveAt(0);
            this.cards.Add(ace);
        }
    }

    private bool IsRoyalFlush()
    {
        return this.Has5OfSameSuit() && this.ToString() == "AKQJT";
    }

    private bool IsStraightFlush()
    {
        return !this.IsRoyalFlush() && this.Has5OfSameSuit() && this.Has5Consecutive();
    }

    private bool IsFourOfAKind()
    {
        var integerValues = this.CardsAsListOfInts().ToList();
        var distinctValues = integerValues.Distinct().ToList();

        if (distinctValues.Count() != 2)
        {
            return false;
        }

        return integerValues.Count(i => i == distinctValues[0]) == 4
            || integerValues.Count(i => i == distinctValues[1]) == 4;
    }

    private bool IsFullHouse()
    {
        return this.CardsAsListOfInts().Distinct().Count() == 2 && !this.IsFourOfAKind();
    }

    private bool IsFlush()
    {
        return this.Has5OfSameSuit() && !this.IsStraightFlush() && !this.IsRoyalFlush();
    }

    private bool IsStraight()
    {
        return this.Has5Consecutive() && !this.IsStraightFlush() && !this.IsRoyalFlush();
    }

    private bool IsThreeOfAKind()
    {
        var integerValues = this.CardsAsListOfInts().ToList();

        // 5 cards are in order of value, therefore middle one integerValues[2] must be value of 3 of a kind or 4 of a kind, if there is one
        return !this.IsFourOfAKind() && integerValues.Count(i => i == integerValues[2]) == 3;
    }

    private bool IsTwoPair()
    {
        return !this.IsThreeOfAKind() && this.CardsAsListOfInts().Distinct().Count() == 3;
    }

    private bool IsPair()
    {
        return this.CardsAsListOfInts().Distinct().Count() == 4;
    }

    private bool Has5OfSameSuit()
    {
        var firstSuit = this.cards.First().Suit;

        return this.cards.All(i => i.Suit == firstSuit);
    }

    private bool Has5Consecutive()
    {
        var ints = this.CardsAsListOfInts().OrderBy(i => i).ToList();
        var altInts = this.CardsAsListOfIntsAceLow().OrderBy(i => i).ToList();
        bool has5 = this.IsConsecutive(ints);
        bool has5Alt = this.IsConsecutive(altInts);

        if (has5Alt)
        {
            int i = 0;
        }
        return has5 || has5Alt;
    }

    private IEnumerable<int> CardsAsListOfIntsAceLow()
    {
        return this.cards.Select(i => i.Value == CardValue.Ace ? 1 : (int)i.Value);
    }

    private IEnumerable<int> CardsAsListOfInts()
    {
        return this.cards.Select(i => (int)i.Value);
    }

    private bool IsConsecutive(IEnumerable<int> integers)
    {
        return !integers.Select((i, j) => i - j).Distinct().Skip(1).Any();
    }

    private void SetCardsBasedOnOrderedSignificantCardsAndKickers(List<Card> significantCards, List<Card> kickers)
    {
        var tempList = new List<Card>();
        tempList.AddRange(significantCards);
        tempList.AddRange(kickers);
        this.cards = tempList;
    }
}

public class HoldemSolution
{
    private Player player1;
    private Player player2;
    private Player winningPlayer;
    private int winningPlayerId;

    public string Solve(List<string> holeCardsPlayer1, List<string> holeCardsPlayer2, List<string> communityCards)
    {
        this.InitializePlayers(holeCardsPlayer1, holeCardsPlayer2, communityCards);
        this.CalculateWinner();

        return this.OutputWinner();
    }

    private void InitializePlayers(List<string> holeCardsPlayer1, List<string> holeCardsPlayer2, List<string> communityCards)
    {
        this.player1 = new Player(Card.ParseCards(holeCardsPlayer1), Card.ParseCards(communityCards));
        this.player2 = new Player(Card.ParseCards(holeCardsPlayer2), Card.ParseCards(communityCards));
    }

    private void CalculateWinner()
    {
        var player1Hand = this.player1.BestFiveCardHand;
        var player2Hand = this.player2.BestFiveCardHand;

        if (this.player1.IsDrawnWith(this.player2))
        {
            this.winningPlayerId = 0;
            this.winningPlayer = this.player1;
        }
        else if (this.player1.IsGreaterThanOrEqualTo(this.player2))
        {
            this.winningPlayerId = 1;
            this.winningPlayer = this.player1;
        }
        else
        {
            this.winningPlayerId = 2;
            this.winningPlayer = this.player2;
        }
    }

    private string OutputWinner()
    {
        // e.g. "1 PAIR AAKQJ"
        string answer = this.winningPlayerId == 0
            ? answer = "DRAW"
            : string.Format("{0} {1} {2}", this.winningPlayerId, this.winningPlayer.HandType.ToString(), this.winningPlayer.BestFiveCardHand.OutputValues());

        return answer;
    }
}

public class Player
{
    private List<Card> sevenCardHand;
    private List<FiveCardHand> fiveCardHands;
    private FiveCardHand bestFiveCardHand;

    public Player(List<Card> holeCards, List<Card> communityCards)
    {
        this.sevenCardHand = holeCards.Union(communityCards).ToList();
        this.fiveCardHands = new List<FiveCardHand>();
        this.CalculateHands();
        this.SelectBestFiveCardHand();
    }

    public HandType HandType { get { return this.bestFiveCardHand.HandType; } }

    public FiveCardHand BestFiveCardHand { get { return this.bestFiveCardHand; } }

    public bool IsGreaterThanOrEqualTo(Player otherHand)
    {
        return this.bestFiveCardHand.IsGreaterThanOrEqualTo(otherHand.BestFiveCardHand);
    }

    private void CalculateHands()
    {
        // Populate list of fiveCardHands with every 5 card combination within sevenCardHand
        foreach (var omittedCardA in this.sevenCardHand)
        {
            foreach (var omittedCardB in this.sevenCardHand)
            {
                if (omittedCardA != omittedCardB)
                {
                    this.fiveCardHands.Add(new FiveCardHand(this.sevenCardHand.Except(new List<Card> { omittedCardA, omittedCardB }).OrderByDescending(i => i.Value).ToList()));
                }
            }
        }

        // Remove duplicates
        this.fiveCardHands = this.fiveCardHands.Distinct().ToList();
    }

    private void SelectBestFiveCardHand()
    {
        this.fiveCardHands.Sort(CompareFiveCardHands);
        this.bestFiveCardHand = this.fiveCardHands.Last();
    }

    private int CompareFiveCardHands(FiveCardHand a, FiveCardHand b)
    {
        return a.IsDrawnWithHand(b) ? 0 : a.IsGreaterThanOrEqualTo(b) ? 1 : -1;
    }

    public bool IsDrawnWith(Player otherHand)
    {
        return this.HandType == otherHand.HandType && this.bestFiveCardHand.OutputValues() == otherHand.bestFiveCardHand.OutputValues();
    }
}
