const n = parseInt(readline())
const p1 = readline().split(" ")
const p2 = readline().split(" ")

const count = {
    "ROCK": {"ROCK": [0,0], "PAPER": [0,1], "SCISSORS": [1,0]},
    "PAPER": {"ROCK": [1,0], "PAPER": [0,0], "SCISSORS": [0,1]},
    "SCISSORS": {"ROCK": [0,1], "PAPER": [1,0], "SCISSORS": [0,0]},
}

const scores = [0, 0]
for(let i = 0; i < n; i++) {
    const [s1, s2] = count[p1[i]][p2[i]]
    scores[0] += s1
    scores[1] += s2
}

console.log(scores.join("-"));
