using System;

class Solution
{
    static void Main(string[] args)
    {
        int B = int.Parse(Console.ReadLine());
        int R = int.Parse(Console.ReadLine());
        string E = Console.ReadLine();
        int W = int.Parse(Console.ReadLine());

        int A = W;

        switch (E)
        {
            case "bp":
                A += 20;
                A *= R;
                break;

            case "lp":
                A += 47;
                A *= R;
                break;

            case "p":
                A += B;
                break;

            default:
                break;
        }

        Console.WriteLine(A);
    }
}
